import React, { useState } from 'react'
import axios from 'axios'
import DatePicker from 'react-datepicker';
// import moment from "moment";
import 'react-datepicker/dist/react-datepicker.css';
import tripName from './Validators';


const ItineraryForm = () => {
    const initialFormData = {
        tripName: '',
        startDate: new Date(),
        endDate: new Date(),
        people:'',
    };

    const [formData, setFormData] = useState(initialFormData);
    const [formSuccess, setFormSuccess] = useState('');
    const [formErrors, setFormErrors] = useState([]);

    const handleSubmit = async(e) => {
        e.preventDefault();
        try {
            // send POST request
            await axios.post('http://localhost:8082/trip', formData);

            // HTTP req successful
            setFormSuccess('Data received correctly');

            // Reset form data
            setFormData(initialFormData);
        } catch(err) {
            handleErrors(err);
        }
    };

    const handleErrors = (err) => {
        if(err.response.data && err.response.data.errors) {
            // Handle validation errors
            const { errors } = err.response.data;

            // Generate an array of error messages using Array.map()
            const errorMsg = errors.map((error) => {
                return error.msg
              });

            setFormErrors(tripName(errorMsg));

        } else {
            // Handle generic error
            setFormErrors(['Oops, there was an error!']);
        }
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.id]: e.target.value,
        });
        setFormErrors([]);
        setFormSuccess('');
    };

    const handleDateChange = (date, dateType) => {
        setFormData({
          ...formData,
          [dateType]: date,
        });
        setFormErrors([]);
        setFormSuccess('');
      };

    return (
        <div className='ItineraryForm'>
            {formErrors.length >1 && (
                <div className='form-errors'>
                    {formErrors.map((error, index) => (
                        <p key={index}>{error}</p>
                    ))}
                </div>
            )}
            <form onSubmit={handleSubmit} className = 'form'>

                <fieldset>
                    <div>
                        <label>Start a new trip!</label>
                            <input 
                                type='text'
                                id='tripName'
                                className="input"
                                placeholder='trip to...'
                                value={formData.tripName}
                                onChange={handleChange}
                                required/>
                    </div>
                    <div>
                        <label>Start Date</label>
                            <DatePicker
                                selected={formData.startDate}
                                onChange={(date) => handleDateChange(date, 'startDate')}/>
                    </div>
                    <div>
                        <label>End Date </label>
                            <DatePicker
                                selected={formData.endDate}
                                minDate={new Date(formData.startDate)}
                                onChange={(date) => handleDateChange(date, 'endDate')}/>
                    </div> 
                    <div>
                        <label>People (Change to invite people later)</label>
                            <textarea
                                type='text'
                                id='people'
                                className="input"
                                placeholder='going with...'
                                value={formData.people}
                                onChange={handleChange}/>
                    </div>
                    <button className="button" type="submit" onClick={handleSubmit}>
                        Create Trip
                    </button>
                </fieldset>
                {formSuccess && <p>{formSuccess}</p>}
            </form>
        </div>
    )
}
export default ItineraryForm
