import { useForm } from "react-hook-form";
import { useRef, useState } from "react";

function ItineraryForm() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    mode: "all",
  });

  //need to rewrite for backend connection + server validation
  // https://www.carlrippon.com/react-hook-form-server-validation/
  const onSubmit = async(data) => {
    if(submitting) {
        return false;
    }

    try {
        setSubmitting(true);
        const response = await axios.post('/api/itineraries', data);
        console.log(response.data);
        setSubmitting(false);
      } catch (error) {
        console.error(error);
        setSubmitting(false);
      }
    console.log(data);

  }
  console.log(errors);

  const [submitting, setSubmitting] = useState(false);

  return (
    <div className="ItineraryForm">
      <form onSubmit={handleSubmit(onSubmit)} autoComplete="off" className="form">
        <fieldset>
        <h2>Start A New Trip!</h2>
        <input
          {...register("tripName", {
            required: "Trip name is Required...",
            minLength: {
              value: 3,
              message: "Trip name must be at least 3 characters long...",
            },
            maxLength: {
              value: 30,
              message: "Trip name must be at most 30 characters long...",
            },
          })}
          placeholder="Trip to..."
        />
        <p>{errors.tripName?.message}</p>
        <input type="Submit" className="button" disabled="submitting"/>
        </fieldset>
      </form>
    </div>
  );
}

export default ItineraryForm;
