import React, { useState } from 'react'
// import axios from 'axios'
import DatePicker from 'react-datepicker';
// import moment from "moment";
import 'react-datepicker/dist/react-datepicker.css';


const ItineraryForm = () => {
    const initialFormData = {
        tripName: '',
        startDate: new Date(),
        endDate: new Date(),
        people:'',
    };

    function isRequired(value) {
        return value != null && value.trim().length > 0;
      }

    const validations = [
        ({tripName}) => isRequired(tripName) || {tripName: "Trip Name is Required"},
        ({startDate, endDate}) => (!startDate || !endDate) || startDate.getDate() <= endDate.getDate() || {endDate: "End Date is not valid"}
    ];

    const onSubmit = () => {};

    function validate(validations, values) {
        const errors = validations
          .map(validation => validation(values))
          .filter(validation => typeof validation === 'object');
        return {isValid: errors.length === 0, errors: errors.reduce((errors, error) => ({...errors, ...error}), {})};
      }

    const {isValid: initialIsValid, errors: initialErrors} = validate(validations, initialFormData);
    const [formData, setFormData] = useState(initialFormData);
    const [formSuccess, setFormSuccess] = useState('');
    const [formErrors, setFormErrors] = useState(initialErrors);
    const [isValid, setValid] = useState(initialIsValid);
    const [touched, setTouched] = useState({});

    const handleSubmit = async(e) => {
        e.preventDefault();
        try {
            // send POST request
            await axios.post('http://localhost:8082/trip', formData);

            // HTTP req successful
            setFormSuccess('Data received correctly');

            // Reset form data
            setFormData(initialFormData);
        } catch(err) {
            handleErrors(err);
        }
    };

    const handleErrors = (err) => {
        const {isValid, errors} = validate(validations, newFormData);
        setFormErrors(errors);
        setValid(isValid);
        // if(err.response.data && err.response.data.errors) {
        //     // Handle validation errors
        //     const { errors } = err.response.data;

        //     // Generate an array of error messages using Array.map()
        //     const errorMsg = errors.map((error) => {
        //         return error.msg
        //       });

        //     setFormErrors(errorMsg);

        // } else {
        //     // Handle generic error
        //     setFormErrors(['Oops, there was an error!']);
        // }
    };

    const handleChange = (e) => {
        const newFormData = {...formData, [e.target.id]: e.target.value};
        const {isValid, errors} = validate(validations, newFormData);
        setFormData(newFormData);
        setFormErrors(errors);
        setFormSuccess('');
        setValid(isValid);
        setTouched({...touched, [e.target.id]: true});
    };

    const handleDateChange = (date, dateType) => {
        const newFormData = {...formData, [dateType]: date};
        const {isValid, errors} = validate(validations, newFormData);
        setFormData(newFormData);
        setFormErrors(errors);
        setFormSuccess('');
        setValid(isValid);
        setTouched({...touched, [dateType]: true});
      };

    return (
        <div className='ItineraryForm'>
            <form onSubmit={handleSubmit} className = 'form'>

                <fieldset>
                    <div>
                        <label>Start a new trip!</label>
                            <input 
                                type='text'
                                id='tripName'
                                className="input"
                                placeholder='trip to...'
                                value={formData.tripName}
                                onChange={handleChange}
                                required/>
                            {touched.tripName && formErrors.tripName && <p className="error">{formErrors.tripName}</p>}
                    </div>
                    <div>
                        <label>Start Date</label>
                            <DatePicker
                                selected={formData.startDate}
                                onChange={(date) => handleDateChange(date, 'startDate')}/>
                    </div>
                    <div>
                        <label>End Date </label>
                            <DatePicker
                                selected={formData.endDate}
                                minDate={new Date(formData.startDate)}
                                onChange={(date) => handleDateChange(date, 'endDate')}/>
                    </div> 
                    <div>
                        <label>People (Change to invite people later)</label>
                            <textarea
                                type='text'
                                id='people'
                                className="input"
                                placeholder='going with...'
                                value={formData.people}
                                onChange={handleChange}/>
                    </div>
                    <button className="button" type="submit" disabled={isValid} onClick={handleSubmit}>
                        Create Trip
                    </button>
                </fieldset>
                {formSuccess && <p>{formSuccess}</p>}
            </form>
        </div>
    )
}
export default ItineraryForm
