import { useForm, Controller } from "react-hook-form";
import ReactDatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import moment from 'moment';

const ItineraryForm = () => {

  const {watch} = useForm();

  const {
    register,
    handleSubmit,
    formState: { errors },
    control
  } = useForm({
    mode: "all",
    defaultValues: {
      startDate: new Date(),
      endDate: new Date(),
      TextField: "",
    }
  });

  const onSubmit = (data, e) => {
    e.preventDefault();
    e.target.reset();
    console.log(data);
    alert(JSON.stringify(data));
  };

  console.log(errors);

  return (
    <div className="ItineraryForm">
      <form onSubmit={handleSubmit(onSubmit)} autoComplete="off" className="form">
        <fieldset>
        <h2>Start a New Adventure!</h2>
        <label>Trip Name</label>
        <input
          {...register("tripName", {
            required: "Trip Name is Required...",
            minLength: {
              value: 3,
              message: "Trip Name must be atleast 3 characters long...",
            },
            maxLength: {
              value: 30,
              message: "Trip Name must be atmost 30 characters long...",
            },
          })}
          placeholder="Trip to..."
        />
        <p>{errors.tripName?.message}</p>
        <label>Start Date</label>
          <Controller
            control={control}
            name="startDate"
            rules = {{ required: true }}
            render={({ field }) => (
              <ReactDatePicker
                className="input"
                placeholderText="Select Start Date"
                onChange={(e) => field.onChange(e)}
                selected={field.value}
              />
              )}
          />
          <p>{errors.startDate && (<span> Start Date is Required...</span>)}</p>
          <label>End Date</label>
          <Controller
            control={control}
            name="endDate"
            rules = {{ 
              required: true,
              validate: watch("endDate") && watch("startDate") && watch("startDate").value <= watch("endDate").value
            }}
            render={({ field }) => (
              <ReactDatePicker
                className="input"
                placeholderText="Select End Date"
                onChange={(e) => field.onChange(e)}
                selected={field.value}
              />
              )}
          />
          {/* <p>{errors.endDate && errors.endDate.type === "required" && (<span> End Date is Required...</span>)}</p> */}
          <p>{errors.endDate && errors.endDate.type === "validate" && (<span> End Date is Invalid...</span>)}</p>
        <button type="Submit" className="button">Create</button>
        </fieldset>
      </form>
    </div>
  );
}

export default ItineraryForm;
