import React from 'react';
import { useForm } from "react-hook-form";
import { withRouter } from "react-router-dom";
import { useStateMachine } from "little-state-machine";
import updateAction from "./updateAction";

const ItineraryForm = (props) => {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    mode: "all",
  });

  const { actions, state } = useStateMachine({ updateAction });
  const onSubmit = (data) => {
    actions.updateAction(data);
    props.history.push("./step2");
  };


  console.log(errors);

  return (
    <div className="ItineraryForm">
      <form onSubmit={handleSubmit(onSubmit)} autoComplete="off" className="form">
        <fieldset>
        <h2>Start a New Adventure!</h2>
        <label>Trip Name</label>
        <input
          {...register("tripName", {
            required: "Trip Name is Required...",
            minLength: {
              value: 3,
              message: "Trip Name must be atleast 3 characters long...",
            },
            maxLength: {
              value: 30,
              message: "Trip Name must be atmost 30 characters long...",
            },
          })}
          placeholder="Trip to..."
        />
        <p>{errors.tripName?.message}</p>
        <button type="Submit" className="button">Create</button>
        </fieldset>
      </form>
    </div>
  );
}

export default withRouter(ItineraryForm);
