import { useForm, Controller} from "react-hook-form";
import ReactDatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";

const ItineraryForm = () => {

  const { watch } = useForm();
  const startDate = watch("startDate");
  const endDate = watch("endDate");
  const validations = {
    tripName: {
      required: "Trip Name is Required...",
      minLength: {
        value: 3,
        message: "Trip Name must be atleast 3 characters long...",
        },
        maxLength: {
          value: 30,
          message: "Trip Name must be atmost 30 characters long...",
        },
    },
    startDate: {
      required: "Start date is required",
    },
    endDate: {
      required: "End date is required",
      validate: (value) => {
        if (value && startDate && value < startDate) {
          return "End date must be after start date";
        }
      },
    },
  };

  const {
    register,
    handleSubmit,
    formState: { errors },
    control
  } = useForm({
    validationSchema: validations,
    mode: "onBlur",
    defaultValues: {
      startDate: new Date(),
      endDate: new Date(),
      tripName: "",
    }
  });


  const onSubmit = (data, e) => {
    e.preventDefault();
    e.target.reset();
    console.log(data);
    alert(JSON.stringify(data));
  };

  console.log(errors);

  return (
    <div className="ItineraryForm">
      <form onSubmit={handleSubmit(onSubmit)} autoComplete="off" className="form">
        <fieldset>
        <h2>Start a New Adventure!</h2>
        <label>Trip Name</label>
        <input name="tripName" ref = {register} placeholder="Trip to..."/>
        <p>{errors.tripName?.message}</p>
        <label>Start Date</label>
          <Controller
            control={control}
            name="startDate"
            ref={register}
            render={({ field }) => (
              <ReactDatePicker
                className="input"
                placeholderText="Select Start date"
                onChange={(e) => field.onChange(e)}
                selected={field.value}
              />
              )}
          />
        <p>{errors.startDate?.message}</p>
        <label>End Date</label>
          <Controller
            control={control}
            name="endDate"
            ref={register}
            render={({ field }) => (
              <ReactDatePicker
                className="input"
                placeholderText="Select End date"
                onChange={(e) => field.onChange(e)}
                selected={field.value}
              />
              )}
          />
          <p>{errors.endDate?.message}</p>
        <button type="Submit" className="button">Create</button>
        </fieldset>
      </form>
    </div>
  );
}

export default ItineraryForm;
