import React, { useState } from 'react'
import axios from 'axios'


const ItineraryForm = () => {
    const initialFormData = {
        tripName: '',
        startDate:'',
        endDate:'',
        people:'',
    };

    const [formData, setFormData] = useState(initialFormData);
    const [formSuccess, setFormSuccess] = useState('');
    const [formErrors, setFormErrors] = useState([]);

    const handleSubmit = async(e) => {
        e.preventDefault();
        try {
            // send POST request
            await axios.post('http://localhost:8082/trip', formData);

            // HTTP req successful
            setFormSuccess('Data received correctly');

            // Reset form data
            setFormData(initialFormData);
        } catch(err) {
            handleErrors(err);
        }
    };

    const handleErrors = (err) => {
        if(err.response.data && err.response.data.errors) {
            // Handle validation errors
            const { errors } = err.response.data;

            // let errorMsg = [];

            // for(let error of errors) {
            //     const { msg } = error;

            //     errorMsg.push(msg);
            // }

            // Generate an array of error messages using Array.map()
            const errorMsg = errors.map((error) => {
                switch (error.param) {
                  case 'tripName':
                    return 'Trip name is required';
                  case 'startDate':
                    return 'Start date is required';
                  case 'endDate':
                    return 'End date is required';
                  case 'startDateFormat':
                    return 'Start date must be in the format DD/MM/YYYY';
                  case 'endDateFormat':
                    return 'End date must be in the format DD/MM/YYYY';
                  case 'startEndDate':
                    return 'Start date must be before end date';
                  default:
                    return 'Oops, there was an error!';
                }
              });

            setFormErrors(errorMsg);
        } else {
            // Handle generic error
            setFormErrors(['Oops, there was an error!']);
        }
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.id]: e.target.value,
        });
        setFormErrors([]);
        setFormSuccess('');
    };

    return (
        <div className='ItineraryForm'>
            <form onSubmit={handleSubmit} className = 'form'>

                <fieldset>
                    <div>
                        <label>Start a new trip!</label>
                            <input 
                                type='text'
                                id='tripName'
                                class="input"
                                placeholder='trip to...'
                                value={formData.tripName}
                                onChange={handleChange}
                                required/>
                    </div>
                    <div>
                        <label>Start Date</label>
                            <input
                                type='text'
                                id='startDate'
                                class="input"
                                placeholder='DD/MM/YYYY'
                                value={formData.startDate}
                                onChange={handleChange}
                                required
                                pattern='\d{2}/\d{2}/\d{4}'/>
                    </div>
                    <div>
                        <label>End Date </label>
                            <input
                                type='text'
                                id='endDate'
                                class="input"
                                placeholder='DD/MM/YYYY'
                                value={formData.endDate}
                                onChange={handleChange}
                                required
                                pattern='\d{2}/\d{2}/\d{4}'
                                />
                    </div> 
                    <div>
                        <label>People </label>
                            <textarea
                                type='text'
                                id='people'
                                class="input"
                                placeholder='going with...'
                                value={formData.people}
                                onChange={handleChange}/>
                    </div>
                    <button type="submit" onClick={handleSubmit}>
                        Create Trip
                    </button>
                </fieldset>
                {formSuccess && <p>{formSuccess}</p>}
                {formErrors && <p>{formErrors}</p>}
            </form>
        </div>
    )
}
export default ItineraryForm
